/**
 * A list of events that can be used as input for this demo.
 * The graph will be extracted from the events and their transitions
 */
export type EventLog = Event[]

/**
 * An event that marks one step in the process.
 */
export type Event = {
  /** The id of the entity that passes through the process steps. */
  caseId: number

  /** The name of the activity that is executed in this event. */
  activity: string

  /** The time when this event starts. */
  timestamp: number

  /** The time this event takes to finish. */
  duration?: number

  /** A cost factor assigned to this event. */
  cost?: number
}
